"use strict"

var exports = module.exports = {};

class Queue {

    constructor() {
        this.size = 0;
        this.input = [];
    }

    addItem(item) {
        this.input.push(item);
        this.size++;
    }

    peek() {
        if(this.size > 0) {
            return this.input[0];
        } else {
            throw new Error("Queue must not be empty");
        }
    }

    pop() {
        if(this.size > 0) {
            var item = this.input.shift();
            --this.size;
            return item;
        } else {
            throw new Error("Queue must not be empty");
        }
    }

    addList(input) {
        if(input) {
            var that = this;
            input.forEach(function (item) {
                that.addItem(item);
            });
        } else {
            throw new Error("input invalid");
        }
    }

}

/**
* Queue
*/
var queue;

/**
 * API
 */

exports.initQueue = function () {
    queue = new Queue(0);
    return queue;
};

